mapper.select:
select apply.*, classes.name as className, teacher.name as teacherName, enterprise.name as enterpriseName, job.name as jobName
from apply
left join classes on apply.class_id = classes.id
left join teacher on apply.teacher_id = teacher.id
left join enterprise on apply.enterprise_id = enterprise.id
left join job on apply.job_id = job.id

     and apply.id= #{id}
     and apply.stu_id= #{stuId}
     and apply.username like concat('%', #{username}, '%')
     and apply.name = #{name}
     and apply.phone= #{phone}
     and apply.salary= #{salary}
     and apply.address= #{address}
     and apply.begin_time= #{beginTime}
     and apply.end_time= #{endTime}
     and apply.status= #{status}
     and apply.read_status= #{readStatus}
     and apply.resume_status= #{resumeStatus}

