mapper.update:
UPDATE apply
SET status = #{status}
WHERE id = #{id}

UPDATE apply
SET read_status = #{readStatus}
WHERE id = #{id}

UPDATE apply
SET apply.resume_status = #{resumeStatus}
WHERE stu_id = #{stuId}
